```javascript
// HomePage.js
const HomePage = async (render) => {
	const homePage = ({ isLoading, products }) => {
		return PageLayout({
		children: () => /*html*/ `
		<!-- 검색 및 필터 -->
		${SearchForm({ isLoading })}
		<!-- 상품 목록 -->
		${ProductList({ isLoading, products })}
		`,
		});
